Scripts.OnLoad:
self.Header:SetText(TMW.L["SORTBY"])

TMW.IE:BuildSimpleCheckSettingFrame(self, {
	numPerRow = 3,
	{
		setting = "Sort",
		value = false,
		title = TMW.L["SORTBYNONE"],
		tooltip = TMW.L["SORTBYNONE_DESC"],
	},
	{
		setting = "Sort",
		value = -1,
		title = TMW.L["ICONMENU_SORTASC"],
		tooltip = TMW.L["ICONMENU_SORTASC_DESC"],
	},
	{
		setting = "Sort",
		value = 1,
		title = TMW.L["ICONMENU_SORTDESC"],
		tooltip = TMW.L["ICONMENU_SORTDESC_DESC"],
	},
})

TMW:CInit(self, {
	alphaSettingName = "Alpha",
	bit = 0x2,
})

TMW:CInit(self, {
	alphaSettingName = "UnAlpha",
	bit = 0x1,
})

TMW:CInit(self, {
	func = TMW.IE.ImportExport_DropDown,
})

self:SetScrollable(true)
self:Hide()

self:SetStatusBarTexture(self.texture) self.texture:SetVertexColor(1, .5, .2, 1)
function self:Set(value, max, nAndMaxN) if not self:IsShown() then self.FadeIn:Play() end self:SetMinMaxValues(0, max) self:SetValue(value)
if self:IsShown() then if nAndMaxN[2] > 1 then self.text:SetFormattedText("%.0f%% (%d/%d)", value/max*100, nAndMaxN[1], nAndMaxN[2]) else self.text:SetFormattedText("%.0f%%", value/max*100) end end
if value >= max and nAndMaxN[1] >= nAndMaxN[2] then self.FadeOut:Play() end end self:SetFrameLevel(self:GetParent():GetFrameLevel()) self.realParent = self:GetParent() --self:SetParent(TellMeWhen_IconEditor)

self:SetBackdrop({
	edgeFile = "Interface\\Tooltips\\UI-Tooltip-Background",
	tile = true,
	edgeSize = 3,
})
self:SetBackdropBorderColor(0, .82, 1, 1)

local parent = self:GetParent()
parent.panelColumns = parent.panelColumns or {}
self.currentPanels = {}
tinsert(parent.panelColumns, self)

TMW:ConvertContainerToScrollFrame(self, true, 1, 5)
self.ScrollFrame.scrollPercentage = 1/3

self:SetText(TMW.L["BACK_IE"]) local desc = function() local icon = TMW.IE.history[TMW.IE.historyState - 1]
return TMW.L["BACK_IE_DESC"]:format(icon:GetIconName(), icon.attributes.texture) end TMW:TT(self, "BACK_IE", desc, nil, 1)
self:SetFrameLevel(self:GetFrameLevel()+2) self:GetNormalTexture():SetTexCoord(0.6875, 0.34375, 0.21875, 0.34375, 0.6875, 0.65625, 0.21875, 0.65625) self:GetHighlightTexture():SetTexCoord(0.6875, 0.34375, 0.21875, 0.34375, 0.6875, 0.65625, 0.21875, 0.65625) self:GetDisabledTexture():SetTexCoord(0.6875, 0.34375, 0.21875, 0.34375, 0.6875, 0.65625, 0.21875, 0.65625) self:SetScale(0.8)

TMW:CInit(self, {
	title = function()
		return TMW.Types[TMW.CI.ics.Type].name
	end,
	tooltip = function()
		return TMW.Types[TMW.CI.ics.Type].desc
	end,
	func = TMW.IE.Type_DropDown,
})

self.Button:HookScript("OnClick", function(self)
	TMW.IE:SaveSettings()
end)

TMW:CInit(self, {
	setting = "Enabled",
	title = TMW.L["ICONMENU_ENABLE"],
	tooltip = TMW.L["ICONMENU_ENABLE_DESC"],
})

TMW:CInit(self, {
	setting = "Controlled",
	title = TMW.L["ICONMENU_CTRLGROUP"],
	tooltip = TMW.L["ICONMENU_CTRLGROUP_DESC"],
	disabledtooltip = TMW.L["ICONMENU_CTRLGROUP_UNAVAILABLE_DESC"],
	disabled = function(self)
		return not TMW.CI.icon.typeData.canControlGroup
	end,
	hidden = function(self)
		return TMW.CI.icon.ID ~= 1
	end,
	OnClick = function(self)
		TMW.CI.group:Setup()
		TMW.IE:Load(1)
	end,
})
function self:GetSettingTable()
	return TMW.CI.group and TMW.CI.group:GetSettings()
end

self:ConstrainLabel(self:GetParent(), "RIGHT")

self:RegisterForDrag("LeftButton")

function self:GetSettingTable()
	return TMW.CI.ics
end

function self:OnSettingSaved()
	return TMW.IE:ScheduleIconSetup()
end

TMW:CInit(self, {
	setting = "Enabled",
	title = TMW.L["ICONMENU_ENABLE"],
	tooltip = TMW.L["ICONMENU_ENABLE_GROUP_DESC"],
})

TMW:CInit(self, {
	setting = TMW.db:GetCurrentProfile(),
	title = TMW.L["ICONMENU_ENABLE_PROFILE"],
	tooltip = TMW.L["ICONMENU_ENABLE_PROFILE_DESC"],

	hidden = function(self)
		return TMW.CI.group.domain == "profile"
	end,
	OnClick = function(self)
		TMW:Update()
		TMW.IE:Load(1)
	end,
})

function self:GetSettingTable()
	return TMW.CI.gs and TMW.CI.gs.EnabledProfiles
end

TMW:RegisterCallback("TMW_GLOBAL_UPDATE", function()
	self.setting = TMW.db:GetCurrentProfile()
end)

self:ConstrainLabel(self:GetParent(), "RIGHT")

self:RegisterForDrag("LeftButton")

function self:GetSettingTable()
	return TMW.CI.gs
end

function self:OnSettingSaved()
	return TMW.CI.group:Setup()
end
