Scripts.OnLoad:
TMW.IE:CreateSettingFrameFromData(self, {
	setting = "ShowCBar",
	title = TMW.L["ICONMENU_ENABLE"],
	tooltip = TMW.L["ICONMENU_SHOWCBAR_DESC"],
})

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "InvertCBar",
	title = TMW.L["ICONMENU_INVERTBARS"],
	tooltip = TMW.L["ICONMENU_INVERTCBAR_DESC"],
	disabled = function(self)
		return not TMW.CI.ics.ShowCBar
	end,
})

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "ShowPBar",
	title = TMW.L["ICONMENU_ENABLE"],
	tooltip = TMW.L["ICONMENU_SHOWPBAR_DESC"],
})

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "InvertPBar",
	title = TMW.L["ICONMENU_INVERTBARS"],
	tooltip = TMW.L["ICONMENU_INVERTPBAR_DESC"],
	disabled = function(self)
		return not TMW.CI.ics.ShowPBar
	end,
	ModifySettingValue = function(self, value)
		return tonumber(value) or 0
	end,
})

self.text:SetText(MINIMUM)
self.setting = "DurationMin"

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "DurationMin",
	title = MINIMUM,
	tooltip = TMW.L["ICONMENU_DURATION_MIN_DESC"],
	disabled = function(self)
		return not TMW.CI.ics.DurationMinEnabled
	end,
	ModifySettingValue = function(self, value)
		return tonumber(value) or 0
	end,
})

self.text:SetText(MAXIMUM)
self.setting = "DurationMax"

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "DurationMax",
	title = MAXIMUM,
	tooltip = TMW.L["ICONMENU_DURATION_MIN_DESC"],
	disabled = function(self)
		return not TMW.CI.ics.DurationMaxEnabled
	end,
	ModifySettingValue = function(self, value)
		return tonumber(value) or 0
	end,
})

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "DurationMinEnabled",
	label = "",
	title = ENABLE,
	tooltip = TMW.L["GENERIC_NUMREQ_CHECK_DESC"]:format(TMW.L["ICONMENU_DURATION_MIN_DESC"]:gsub("^%u", strlower)),
})


TMW.IE:CreateSettingFrameFromData(self, {
	setting = "DurationMaxEnabled",
	label = "",
	title = ENABLE,
	tooltip = TMW.L["GENERIC_NUMREQ_CHECK_DESC"]:format(TMW.L["ICONMENU_DURATION_MAX_DESC"]:gsub("^%u", strlower)),
})

