self.text:SetWidth(TMW.WidthCol1)

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "OnlyIfCounting",
	title = TMW.L["ICONMENU_ONLYIFCOUNTING"],
	tooltip = TMW.L["ICONMENU_ONLYIFCOUNTING_DESC"],
})

self.Header:SetText(TMW.L["SORTBY"])
TMW.IE:BuildSimpleCheckSettingFrame(self, {
	{
		setting = "Sort",
		value = false,
		title = TMW.L["SORTBYNONE"],
		tooltip = TMW.L["SORTBYNONE_DESC"],
	},
	{
		setting = "Sort",
		value = -1,
		title = TMW.L["ICONMENU_SORTASC"],
		tooltip = TMW.L["ICONMENU_SORTASC_DESC"],
	},
	{
		setting = "Sort",
		value = 1,
		title = TMW.L["ICONMENU_SORTDESC"],
		tooltip = TMW.L["ICONMENU_SORTDESC_DESC"],
	},
})

Scripts.OnEnter:
local parent = self:GetParent();
local myscript = parent:GetScript("OnEnter");
if(myscript ~= nil) then
	myscript(parent);
end

Scripts.OnLeave:
local parent = self:GetParent();
local myscript = parent:GetScript("OnLeave");
if(myscript ~= nil) then
	myscript(parent);
end

Scripts.OnLoad:
self.text:SetWidth(85)
self:SetFrameLevel(self:GetFrameLevel() + 2) -- sometimes it ends up under the type dropdown which makes half of the checkbox unclickable

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "Enabled",
	title = TMW.L["ICONMENU_ENABLE"],
})

self.text:SetWidth(100)

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "FakeHidden",
	title = TMW.L["ICONALPHAPANEL_FAKEHIDDEN"],
	tooltip = TMW.L["ICONALPHAPANEL_FAKEHIDDEN_DESC"],
})

self.text:SetWidth(self:GetParent().FakeHidden.text:GetWidth())

TMW.IE:CreateSettingFrameFromData(self, {
	setting = "CheckNext",
	title = TMW.L["ICONMENU_CHECKNEXT"],
	tooltip = TMW.L["ICONMENU_CHECKNEXT_DESC"],
})
