Scripts.OnEnter:
local parent = self:GetParent();
local myscript = parent:GetScript("OnEnter");
if(myscript ~= nil) then
	myscript(parent);
end

Scripts.OnLeave:
local parent = self:GetParent();
local myscript = parent:GetScript("OnLeave");
if(myscript ~= nil) then
	myscript(parent);
end

Scripts.OnEditFocusLost:
EditBox_ClearHighlight(self)
local t = TMW:CleanString(self) t = floor(tonumber(t) or 0) self:SetText(t)
if TMW.CI.ics then TMW.CI.ics.StackMin = t TMW.IE:ScheduleIconUpdate(TMW.CI.ic) end self:GetScript("OnTextChanged")(self)

EditBox_ClearHighlight(self)
local t = TMW:CleanString(self) t = floor(tonumber(t) or 0) self:SetText(t)
if TMW.CI.ics then TMW.CI.ics.StackMax = t TMW.IE:ScheduleIconUpdate(TMW.CI.ic) end self:GetScript("OnTextChanged")(self)
